body.raw:
{
  "query": "query { movies { id name director_name production_house release_date rating } }"
}

{
  "query": "query ($id: ID!) { movie(id: $id) { id name director_name production_house release_date rating } }",
  "variables": { "id": "YOUR_MOVIE_ID" }
}

{
  "query": "query ($name: String!) { moviesByDirector(director_name: $name) { id name director_name production_house release_date rating } }",
  "variables": { "name": "Christopher Nolan" }
}

{
  "query": "mutation ($name: String!, $director: String!, $house: String!, $date: String!, $rating: Float!) { addMovie(name: $name, director_name: $director, production_house: $house, release_date: $date, rating: $rating) { id name director_name production_house release_date rating } }",
  "variables": {
    "name": "Interstellar",
    "director": "Christopher Nolan",
    "house": "Paramount Pictures",
    "date": "2014-11-07",
    "rating": 8.6
  }
}

{
  "query": "mutation ($id: ID!, $name: String, $director: String, $house: String, $date: String, $rating: Float) { updateMovie(id: $id, name: $name, director_name: $director, production_house: $house, release_date: $date, rating: $rating) { id name director_name production_house release_date rating } }",
  "variables": {
    "id": "YOUR_MOVIE_ID",
    "name": "Interstellar (Updated)",
    "director": "Christopher Nolan",
    "house": "Warner Bros.",
    "date": "2014-11-07",
    "rating": 9.0
  }
}

{
  "query": "mutation ($id: ID!) { deleteMovie(id: $id) { id name director_name production_house release_date rating } }",
  "variables": { "id": "YOUR_MOVIE_ID" }
}
